<script>
    import Bus from '../../../bus';

    export default {
        data() {
            return {
                lines: 1,
                profit: 0,
                icons: ['apple', 'bananas', 'cherry', 'grapes', 'orange', 'pineapple', 'strawberry', 'watermelon', 'lemon', 'kiwi', 'raspberry', 'wild', 'scatter'],

                hoverId: null
            }
        },
        watch: {
            lines() {
                this.recalculate();
            },
            profit() {
                Bus.$emit('profit:update', this.profit);
            }
        },
        mounted() {
            const instance = this;
            $('[data-line-path]').on('mouseover', function() {
                $('.deleteMe').remove();

                const json = Array.from($(this).data('line-path'));
                const slot = (x, y) => $($('.slots .column')[x]).find(`.columnIconContainer:nth-child(${y + 1}) .icon`);

                for(let i = 0; i < json.length; i++) {
                    if(json[i + 1] === undefined) continue;
                    instance.makeLine(slot(json[i][0], json[i][1]), slot(json[i + 1][0], json[i + 1][1]));
                }
            });

            $('[data-line-path]').on('mouseleave', () => {
                $('.deleteMe').remove();
                this.hoverId = null;
            });

            const slots = $('.game-slots').find('.slots'), cols = $('.game-slots').find('.column');

            $.each(cols, (i, e) => {
                let str = '', firstThree = '';
                for(let x = 0; x < 30; x++) {
                    const icon = this.icons[Math.floor(Math.random() * this.icons.length)],
                        part = `<div class="columnIconContainer">
                            <svg class="icon"><use href="#icon-${icon}"></use></svg>
                        </div>`;
                    str += part;
                    if(x < 3) firstThree += part;
                }
                e.innerHTML = str + firstThree;
            });

            window.slotsSpin = (response) => {
                slots.addClass('spinning');
                $('[data-line]').trigger('mouseleave');

                $('.scatter, .wild').fadeOut('fast', function() {
                    $(this).remove();
                });

                setTimeout(() => {
                    $.each(cols, (i, e) => {
                        const svgIcons = $(e).find('.icon use');
                        for(let x = 0; x < 3; x++) {
                            const icon = response.game.data.result[i][x];
                            $(svgIcons[x]).attr('href', `#icon-${icon}`);
                            $(svgIcons[(svgIcons.length - 3) + x]).attr('href', `#icon-${icon}`);
                        }
                    });
                }, 1000);

                setTimeout(() => {
                    slots.removeClass('spinning');
                    this.updateGameInstance((i) => i.playTimeout = false);
                    this.resultPopup(response.game);

                    $('use[href="#icon-scatter"]').parent().parent().append($(`<div class="scatter">SCATTER</div>`).hide().fadeIn('fast'));
                    $('use[href="#icon-wild"]').parent().parent().append($(`<div class="wild">WILD</div>`).hide().fadeIn('fast'));

                    _.forEach(response.game.data.lines, (e) => {
                        const json = Array.from($(`[data-line="${e}"]`).data('line-path'));
                        const slot = (x, y) => $($('.slots .column')[x]).find(`.columnIconContainer:nth-child(${y + 1}) .icon`);

                        for(let i = 0; i < json.length; i++) {
                            if(json[i + 1] === undefined) continue;
                            this.makeLine(slot(json[i][0], json[i][1]), slot(json[i + 1][0], json[i + 1][1]));
                        }
                    });
                }, 3500);
            }

            Bus.$on('sidebar:update', this.recalculate);
            Bus.$emit('profit:update', 0);
        },
        methods: {
            callback(response) {
                window.slotsSpin(response);
            },
            errorCallback() {
                this.$toast.error(this.$i18n.t('general.invalid_amount_per_line'));
            },
            getClientData() {
                return {
                    lines: this.lines
                }
            },
            recalculate() {
                $.each($('.line'), (i, e) => $(e).toggleClass('disabled', parseInt($(e).html()) > this.lines));
                this.profit = this.gameInstance.bet / this.lines;
            },
            makeLine(div1, div2) {
                const line = $($('.svg_line')[0]),
                    clone = line.clone(),
                    x0 = $('.lineContainer').position().left,
                    y0 = $('.lineContainer').position().top,
                    x1 = (div1.position().left - x0) + (div1.width() / 2),
                    y1 = (div1.position().top - y0)  + (div1.height() / 2),
                    x2 = (div2.position().left - x0) + (div2.width() / 2),
                    y2 = (div2.position().top - y0)  + (div2.height() / 2);

                clone.addClass('deleteMe').removeClass("original")
                    .attr('x1', x1).attr('y1', y1).attr('x2', x2).attr('y2', y2).insertAfter(line);
            },
            getSidebarComponents() {
                return [
                    { name: 'label', data: { label: this.$i18n.t('general.wager') } },
                    { name: 'wager-classic' },
                    { name: 'label', data: { label: this.$i18n.t('general.lines') } },
                    { name: 'buttons', data: { buttons: [
                                { label: 1, callback: () => this.lines = 1 },
                                { label: 5, callback: () => this.lines = 5 },
                                { label: 10, callback: () => this.lines = 10 },
                                { label: 15, callback: () => this.lines = 15 },
                                { label: this.$i18n.t('general.edit'), callback: (v) => this.lines = v, type: 'input', input: { min: 1, max: 20 } } ] } },
                    { name: 'label', data: { label: this.$i18n.t('general.profit_per_line') } },
                    { name: 'profit' },
                    { name: 'auto-bets' },
                    { name: 'play' },
                    { name: 'footer', data: { buttons: ['sound', 'stats'] } }
                ];
            }
        }
    }
</script>

